    /**
     * @covers JFile::exists
     */
	public function testExists() {
		$this->assertTrue(
			JFile::exists(__FILE__)
		);
	}

    /**
     * @covers JFile::getName
     */
	public function testGetName() {
		$this->assertEquals(
			'file.php',
			JFile::getName('C:\path\on\windows\file.php')
		);

		$this->assertEquals(
			'image.png',
			JFile::getName('/full/path/image.png')
		);

		$this->assertEquals(
			'nopath.csv',
			JFile::getName('nopath.csv')
		);
